list-view-config.query-base:

PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX afn:  <http://jena.hpl.hp.com/ARQ/function#>

SELECT ?object ?label ?localname ?moniker WHERE {
    ?subject ?property ?object
        LET (?localname := afn:localname(?object))
    OPTIONAL { ?object rdfs:label ?label }
    OPTIONAL { ?object vitro:moniker ?moniker }
} ORDER BY ?name ?object

list-view-config.query-collated:

PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX afn:  <http://jena.hpl.hp.com/ARQ/function#>

SELECT ?subclass ?object ?label ?localname ?moniker {
    ?subject ?property ?object
        LET (?localname := afn:localname(?object))
    OPTIONAL { ?object a ?subclass } 
    OPTIONAL { ?object rdfs:label ?label }
    OPTIONAL { ?object vitro:moniker ?moniker }           
    FILTER ( afn:namespace(?subclass) != "http://vitro.mannlib.cornell.edu/ns/vitro/0.7#" )                     
} ORDER BY ?subclass ?name ?object

list-view-config.query-construct:
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  

CONSTRUCT {
    ?subject ?property ?object .
    ?object a ?subclass .
    ?object rdfs:label ?name .
    ?object vitro:moniker ?moniker
} WHERE {
    { 
        ?subject ?property ?object 
    }
    UNION {
        ?subject ?property ?object .
        ?object a ?subclass . 
    } 
    UNION {
        ?subject ?property ?object .
        ?object rdfs:label ?name .
    } 
    UNION {
        ?subject ?property ?object .
        ?object vitro:moniker ?moniker .
    }
}
